class Solution:

    # UNFINISHED    FIX ME
    def highestPeak(self, isWater: list[list[int]]) -> list[list[int]]:
        m = len(isWater)
        n = len(isWater[0])
        water = []
        border = []

        print(time.time())
        for i in range(m):
            for j in range(n):
                isWater[i][j] = 0 if isWater[i][j] == 1 else 1
                if isWater[i][j] == 0:
                    water.append((i,j))

        """print(time.time())
        for i,j in water:
                neighbors = [(i-1,j),(i+1,j),(i,j-1),(i,j+1)]
                has_land = False    
                for x,y in neighbors:
                    if 0 <= x < m and 0 <= y < n:
                        if isWater[x][y] == 1:
                            border.append((i,j))
                            break
        """

        for br,bc in water:
            q = [(br,bc)]
            visited = set([(br,bc)])        
            #print(time.time(), isWater)

            while q:
                r,c = q.pop(0)
                heights = []
                neighbors = [(r-1,c),(r+1,c),(r,c-1),(r,c+1)]

                for i,j in neighbors:
                    if 0 <= i < m and 0 <= j < n:
                        heights.append(isWater[i][j])
                        if (i,j) not in visited:
                            q.append((i,j))
                            visited.add((i,j))
                
                min_height = min(heights)
                if heights and isWater[r][c] != 0:
                    if isWater[r][c] != min_height+1:
                        isWater[r][c] = min_height + 1
                        # add r,c to the back of the queue
                        q.append((r,c))




        return isWater


